package chap02;

public class SumAve1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x;
		int y;
		
		x = 63;
		y = 18;
		System.out.println("x的值是： "+ x + "。");
		System.out.println("y的值是： "+ y + "。");
		System.out.println("合计值是： "+ x+y + "。");
		System.out.println("合计值是： "+ (x+y) + "。");
		System.out.println("平均值是： "+ (x+y)/2 + "。");
	}

}
